import speech from '@google-cloud/speech'
import fs from 'fs'
import request from 'request'
import Logger from '../../logger'
import { USING_VISION } from './controller'
const TEMP_AUDIO_PATH = 'temp.aac'
/**
 * Transcribe the audio into text
 * @param {URI of the recorded audio file} AudioUrl
 */
const speechToText = (AudioUrl: string): Promise<string> => {
	return new Promise((resolve, reject) => {
		if (!USING_VISION) { resolve('Nice voice you\'ve got there!') } else {
			Logger.info('Transcribing speech to text...')
			const client = new speech.SpeechClient()

			getAudio(AudioUrl).then(() => {
				const file = fs.readFileSync(TEMP_AUDIO_PATH)
				const audioBytes = file.toString('base64')
				// The audio file's encoding, sample rate in hertz, and BCP-47 language code
				const audio = {
					content: audioBytes,
				}
				const config = {
					encoding: 'LINEAR16',
					sampleRateHertz: 16000,
					languageCode: 'en-US',
				}
				const request = {
					audio,
					config,
				}

				// Detects speech in the audio file
				client
					.recognize(request)
					.then((data) => {
						const response = data[0]
						const transcription = response.results
							.map((result) => result.alternatives[0].transcript)
							.join('\n')
						resolve(transcription)
					})
					.catch((err) => {
						reject(err)
					})
			})
		}

	})
}

const getAudio = (AudioUrl: string): Promise<any> => {
	return new Promise((resolve) => {
		request.head(AudioUrl, () => {
			request(AudioUrl).pipe(fs.createWriteStream(TEMP_AUDIO_PATH)).on('close', () => resolve())
		})
	})
}

export {
	speechToText,
}

import * as tf from '@tensorflow/tfjs'
import '@tensorflow/tfjs-node'
import { characterLevelEmbed, toWordIntent } from './utils'
let model = null
const MODEL_FILE_PATH = 'file://dist/main/externalApis/@google/tensorflow/model.json'

/**
 * Predict the intent of a message
 * @param {string} s 
 * @return {{intent: string, confidence: number}}
 */
const predict = async (s: string): Promise<{intent: string, confidence: number}> => {
	try {
		if (!model) { model = await tf.loadModel(MODEL_FILE_PATH) }
		const x = []
		x.push(characterLevelEmbed(s))
		const inputTensor: tf.Tensor<tf.Rank.R3> = tf.tensor3d(x)
		const res = await model.predict(inputTensor)
		const possibility: number[] =  Array.from(res.dataSync())
		const maxIdx: number = Math.max(...possibility)
		const idx: number = possibility.indexOf(maxIdx)
		const intent = toWordIntent(idx)
		return {
			intent,
        	confidence: maxIdx,
		}
	} catch (e) {
		return Promise.reject(e)
	}

}

export {
	predict,
}

const allASCIIletters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ .,;\''
const numLetters = allASCIIletters.length
/**
 * Convert intent number to string
 * @param {number} n Number to intent
 * @return {string} intent
 */
const toWordIntent = (n: number): string => {
	switch (n) {
	case 0:
		return 'greetings'
	case 1:
		return 'thanks'
	case 2:
		return 'bye'
	case 3:
		return 'news'
	case 4:
		return 'weather'
	case 5:
		return 'worldCup'
	case 6:
		return 'pkmGO'
	case 7:
		return 'help'
	case 8:
		return 'compliment'
	}

}

/**
 * Vector array with absolute index of 1-hot vectors
 * @param {Array<number>} sentence of index
 * @return {Array<Array<number>>} array of 1-hot vector
 */
const sentenceToOneHotVectors = (sentence: number[]): number[][] => {
	const res = []
	for (const idx of sentence) {
		const arr = new Array(numLetters + 1).fill(0)
		arr[idx] = 1
		res.push(arr)
	}
	return res
}

/**
 * Convert letter to index based on allASCIIletters
 * @param {string} letter
 */
const letterToIndex = (letter: string): number =>  allASCIIletters.indexOf(letter) + 1

/**
 * Convert sentence to array of indices
 * @param sentence
 */
const sentenceToIndex = (sentence: string): number[] => {
	const res = []
	for (let i = 0; i < sentence.length; i++) { res.push(letterToIndex(sentence[i])) }
	return res
}

/**
 * Create a 2-D array of 0z
 * @param row number of row
 * @param col number of col
 */
const zeros = (row: number, col: number): number[][] => Array.from(Array(row), (_) => Array(col).fill(0))

/**
 * embed the original string with one-hot vector and 0-filling
 * @param X original string
 */
const characterLevelEmbed = (X: string): number[][] => {
	const x: string = X.toLowerCase()
	const xIndices: number[] = sentenceToIndex(X)
	const ohVector: number[][] = sentenceToOneHotVectors(xIndices)
	const toConcat: number[][] = zeros(100 - x.length, numLetters + 1)
	return ohVector.concat(toConcat)
}
export {
	toWordIntent,
	characterLevelEmbed,
}

