export default class Headquarter {
	/**
	 * Handle receving events
	 * @param platform platforms currently supported
	 * @param payload message payload from user
	 */
	public async receive(platform: supportedPlatform, payload: any): Promise<void | Error> {
		Logger.info('Transfering event to headquarter..')
		try {
			const partialUniqueId = await identifySource(platform, payload)
			let user = await retrieveOrCreateUser(partialUniqueId, platform, payload)
			user = await analyze(platform, payload, user)
			user = await execute(platform, payload, user)
			await respond(platform, payload, user)
			await saveUser(user.id, user)
		} catch (err) {
			Logger.error(err)
		} finally {
			Logger.separator('=====================================')
		}
	}
}

import idx from 'idx'
import Logger from './logger'
import { generateRandomId } from './utils/string'
/**
 * Return a partial unique userId from incoming event to identify user
 * @param {supportedPlatform} platform supported platform currently
 * @param {any} payload
 */
export default async (platform: supportedPlatform, payload: any): Promise<string> => {
	const log = Logger.info('Identifying source...', true)
	const data = {
		hashPrefix: '',
		id: '',
	}
	switch (platform) {
	case 'telegram':
		if (idx(payload, (_) => _.from.id)) {
			data.hashPrefix = 'tlg',
			data.id = idx(payload, (_) => _.from.id)
		}
		break

	case 'messenger':
		if (idx(payload, (_) => _.sender.id)) {
			data.hashPrefix = 'mes',
			data.id = idx(payload, (_) => _.sender.id)
		}
		break

	default:
		data.hashPrefix = 'gia'
		// need to make Id more deterministic to prevent collision
		data.id = generateRandomId()

	}
	log.stop('Indentified Source.')
	return `${data.hashPrefix}${data.id}`
}

if (process.env.NODE_ENV === 'production') { require('dotenv').load() }
import cryptoJSON from 'crypto-json'

const cipher = process.env.CIPHER
const passKey = process.env.PASSKEY
const encoding = 'hex'

/**
 * Encode JSON object with hidden cipher
 * @param {any} object
 */
const encrypt = (object: any) =>  {
	if (typeof object !== 'object') {
		throw new Error('Cannot encrypt non-object')
	}
	return cryptoJSON.encrypt(object, passKey, {
		algorithm: cipher,
		encoding,
		keys: [],
	})
}
/**
 * Decode JSON object with cipher
 * @param {any} object
 */
const decrypt = (object: any) => {
	if (typeof object !== 'object') { throw new Error('Cannot decrypt non-object: ' + object) }
	return cryptoJSON.decrypt(object, passKey, {
		algorithm: cipher,
		encoding,
		keys: [],
	})
}
export {
	encrypt,
	decrypt,
}

