	}

}

import Logger from './logger'
import { getUserName } from './messenger/api/graphApi'
import {
	getUser,
	saveUser,
} from './model/cache'
import UserDB from './model/mongoDB/user'

/**
* Return a partial unique userId from incoming event to identify user
* @param {supportedPlatform} platform supported platform currently
* @param {any} payload
* @return promise contains the updated user
*/
export default async (partialUniqueId: string, platform: supportedPlatform, payload: any): Promise<userType> => {
	try {
		const user = await getUser(partialUniqueId)
		if (user) {
			return user
		} else {
			const newUser = await createNewUser(partialUniqueId, platform, payload)
			await UserDB.addUser(newUser)
			saveUser(partialUniqueId, newUser)
			return newUser
		}
	} catch (e) {
		return Promise.reject(e)
	}

}

/**
 * Create new user based on the payload
 * @param {string} partialUniqueId
 * @param {supportedPlatform} platform
 * @param {any} payload
 * @return user object
 */
const createNewUser = async (partialUniqueId: string, platform: supportedPlatform, payload: any): Promise<userType> => {

	try {
		const log = Logger.info('Creating new user...', true)
		const user: userType = new UserDB({
			id: partialUniqueId,
			locale: 'eng',
			entity: {
				lastIntent: null,
			},
		})
		let name
		switch (platform) {
		case 'telegram':
			user.name = {
				first: payload.from.first_name,
			}
			break

		case 'messenger':
			name = await getUserName(user.id.replace('mes', ''))
			if (name) {
				const { firstName, lastName } = name
				user.name = {
					first: firstName,
					last: lastName,
					full: `${firstName} ${lastName}`,
				}
			}
			break

		default:

		}
		log.stop('Created new user.')
		return user
	} catch (e) {
		return Promise.reject(e)
	}

}

import { getPlatformAndId } from './../utils/account'
import Logger from '../logger'
import MesMessage from '../messenger/api/message'
import MesSendImage from '../messenger/api/sendImage'
import { tlgImage, tlgMessage }   from '../telegram/api/'


interface StreamableObject {
	text?: string,
	image?: string
}
/**
 * 
 * @param toStream object to stream
 * @param firebase ref to firebase
 */
const stream = (toStream: StreamableObject, firebase: any):void => {
	firebase.database().ref('restricted_access/streaming/').once('value').then(snap => {
		const result = snap.val()
		let audience
		if (Array.isArray(result)) audience = result
		else if (typeof result === 'object') audience = Object.values(result)
		for (let userId of audience) {
			const {
				platform,
				id,
			} = getPlatformAndId(userId)
			let message, image
			if (platform === 'messenger') {
				message = MesMessage
				image = MesSendImage
			} else {
				message = tlgMessage
				image = tlgImage
			}
			if (toStream.text) message(id, toStream.text)
			if (toStream.image) image(id, toStream.image) 
		}
	}, (err) => {
		Logger.error(err)
	})
}

export default stream

import idx from  'idx'
import { Observable } from 'rxjs'
import Logger from '../logger'
import { client } from '../externalApis/@twitter'
import stream from './'

const POKEMONGO_TWITTER = '2839430431'
const WYK_TWITTER = '44680622'
const MY_TWITTER = '700623960'
const listOfStreams = [
	POKEMONGO_TWITTER,
	WYK_TWITTER,
	MY_TWITTER,
]

interface OberservableTweet {
	name: string
	text: string
	image: string
	audience: string[]
}

/**
 * Class for twitter streaming
 */
export default class TwitterStreaming {
	public streaming
	public twitterStreaming

	/**
	 * Constructor for twitter streaming class
	 */
	constructor() {
		this.streaming = Observable.create((observer) => {
			const twitter = client.stream('statuses/filter', {follow: listOfStreams.join(',')})
			twitter.on('data', (tweet) => {
				if (listOfStreams.includes(tweet.user.id_str)) {
					observer.next({
						name: tweet.user.name,
						text: tweet.text,
						image: idx(tweet, (_) => _.extended_entities.media[0].media_url_https) || null
					})
				}
			})
			twitter.on('error', (error) => {
				observer.error(error)
			})

			return () => {
				twitter.destroy()
			}
		})
	}

	/**
	 * Start listenning to twitter streaming api
	 * @param firebase ref to firebase module
	 */
	public startStreaming(firebase: any):void {
		Logger.info('Starting twitter stream')
		this.twitterStreaming = this.streaming.subscribe({
			next: (x: OberservableTweet) => {
				stream({
					text: x.name + ': ' + x.text,
					image: x.image
				}, firebase)
			},
			error: (err) => Logger.error('something wrong occurred: ' + err),
			complete: () => Logger.info('done'),
		})
	}

	/**
	 * Terminate the stream
	 */
	public stopStreaming(): void {
		Logger.warn('Stopping Twitter stream')
		this.twitterStreaming.unsubscribe()	
	}
}

