interface Result {
	platform: string
	id: string | number
}
/**
 * Get the platform and platform-specific id from the database string
 * @param {string} databaseId
 * @return {Result} parsed data
 * @return an object containing the account id and platform
 */
const getPlatformAndId = (databaseId: string): Result => {
	let platform: string
	let id: string | number
	if (databaseId.indexOf('mes') !== -1) {
		platform = 'messenger'
		id = databaseId.replace('mes', '')
	} else if (databaseId.indexOf('tlg') !== -1) {
		platform = 'telegram'
		id = parseInt(databaseId.replace('tlg', ''))
	} else if (databaseId.indexOf('gia') !== -1) {
		platform = 'unknown'
		id = databaseId.replace('gia', '')
	}

	return {
		platform,
		id,
	}
}
export {
	getPlatformAndId,
}

/**
 * Check duplicate of a sorted array.
 * @param {any[]} a
 * @return true if there are duplicated. False otherwise
 */
const checkDuplicates = (a: any[]): boolean => {
	for (let i = 0; i < a.length - 1; i++) {
		if (a[i] === a[i + 1]) { return true }
	}
	return false
}
/**
 * Check if two arrays has any mutual member
 * @param {any[]} array1
 * @param {any[]} array2
 * @return True if two arrays have mutual elements. False otherwise.
 */
const checkIfArrayMutual = (array1: any[], array2: any[]) => {
	return array1.filter((value) => -1 !== array2.indexOf(value)).length > 0
}

/**
 * Get the list of common members from 2 arrays
 * @param {any[]} a
 * @param {any[]} b
 * @return array containing all the mutual elements
 */
const getCommonMembersFromTwoArrays = (a: any[], b: any[]): any[] => a.filter((n) => b.indexOf(n) !== -1 )

/**
 * shuffle an array based on Fisher-Yates (aka Knuth) Shuffle algorithm
 * @param {any[]} array
 * @return array shuffled
 */
const shuffle = (array: any[]): any[] => {
	let currentIndex = array.length, temporaryValue, randomIndex
	// While there remain elements to shuffle...
	while (0 !== currentIndex) {
		// Pick a remaining element...
		randomIndex = Math.floor(Math.random() * currentIndex)
		currentIndex -= 1
		// And swap it with the current element.
		temporaryValue = array[currentIndex]
		array[currentIndex] = array[randomIndex]
		array[randomIndex] = temporaryValue
	}
	return array
}

/**
 * Check if an  array of objects hay any object that contains a key with a specific attribute value.
 * @param {any[]} arr
 * @param {string} attribute
 * @param {string} key
 * @return object with key equal to some values, null if there is no such object
 */
const containsObjectWithNameAttribute = (arr: any[], attribute: string, key: string = 'name'): any => {
	for (const object of arr) {
		if (object[key] === attribute) {
			return object
		}
	}
	return false
}

/**
 * Return a random member of an array
 * @param {any[]} array
 * @return an element inside the array
 */
const randomIndex = <T extends {}>(array: T[]) => array[Math.floor(Math.random() * array.length)]

/**
 * Remove an element from array
 * @param {any[]}array Array to be process
 * @param {any} member elemen to be removed
 * @return a new array that doesn't have the old element
 */
const removeFromArray  = <T extends {}>(array: T[], member: T) => {
	const index = array.indexOf(member)
	if (index > -1) { array.splice(index, 1) }
	return array
}
export {
	checkDuplicates,
	checkIfArrayMutual,
	shuffle,
	getCommonMembersFromTwoArrays,
	containsObjectWithNameAttribute,
	randomIndex,
	removeFromArray,
}

import Logger from '../logger'
/**
 * Mask an object with an mask.
 * @param target Object to be masked
 * @param mask mask
 * @returns masked results;
 * @throws Error if target is not an object;
 */
const maskObject = (target: any, mask: object): (object | Error) => {
	if (!target || !mask) throw new Error('maskObject: Argument missing!')
	if (Array.isArray(target) || typeof(target) !== 'object') throw new Error('maskObject: Target object must be an object!')
	const res = {}
	for (const key in target) {
		if (mask[key] == true) res[key] = target[key]

		else if (typeof target[key] != 'object' && typeof mask[key] != 'boolean')
			Logger.warn('Object ' + target
				+ ' at key ' + key + ' is not an object'
				+ 'but mask ' + mask + ' at this key is an object')

		else if (typeof target[key] == 'object' && typeof mask[key] == 'object') {
			const val = maskObject(target[key], mask[key])
			res[key] = val
		}
	}
	return res
}

export {
	maskObject,
}

import natural from 'natural'
natural.PorterStemmer.attach()
/**
* Tokenized text and return an array of stem words
* NOTE: Quite unreliable
* @param {string} text
* @return array of tokenized text
*/
const tokenizeText = (text: natural.String): string[] => text.toLowerCase().tokenizeAndStem()

/**
* Right padding s with c to a total of n chars
* @param {string} s original string
* @param {string} c character to be padded
* @param {number} n length
* @return string with right side padded
*/
const padRight = (s: string, c: string, n: number): string => {
	if (! s || ! c || s.length >= n) {
		return s
	}
	const max = (n - s.length) / c.length
	for (let i = 0; i < max; i++) {
		s += c
	}
	return s
}

/**
* Left padding s with c to a total of n chars
* @param {string} s original string
* @param {string} c character to be padded
* @param {number} n length
* @return a string with left side padded
*/
const padLeft = (s: string, c: string, n: number): string => {
	if (! s || ! c || s.length >= n) {
		return s
	}
	const max = (n - s.length) / c.length
	for (let i = 0; i < max; i++) {
		s = c + s
	}
	return s
}

/**
* Remove all substrings inside a string
* @param {string} s original string
* @param {string} substrings substrings to be removed
* @return a string with all substrings removed
*/
const replaceAllSubstring = (s: string, ...substrings: string[]): string => {
	for (const substring of substrings) { s = replaceAll(s, substring, '') }
	return s
}

/**
* Return a numeric substring from a string
* @param {string} s original strong
* @param {number} index index of the numeric subtring to be returned. default is 0
* @return a string that is convertible to a number
*/
const getNumberStringFromString = (s: string, index?: number): string | null => {
	const i = index ? index : 0
	return s.match(/\d+/)[i] ? s.match(/\d+/)[i] : s.match(/\d+/)[0] ? s.match(/\d+/)[0] : null
}

/**
* Generate a random string of numbers based on length given
* @param {number} length
* @return a random string consists of n umber
*/
const generateRandomId = (length: number = 10): string => {
	let text = ''
	const possible = '0123456789'
	for (let i = 0; i < length; i++) text += possible.charAt(Math.floor(Math.random() * possible.length))
	return text
}

/**
* Replace all occurence of a substring in a string
* @param {string} str
* @param {string} find
* @param {string} replace
* @return a string will all `find` replaced by `replace`
*/
const replaceAll = (str, find, replace): string => {
	return str.replace(new RegExp(find, 'g'), replace)
}

export {
	padRight,
	padLeft,
	replaceAllSubstring,
	getNumberStringFromString,
	generateRandomId,
	tokenizeText,
	replaceAll,
}

/**
 * Wait for a certain amount to time to execute some function. Clean up version of setTimeOut
 * @param {number} time Amount of miniseconds to wait
 * @param {function} cb callback function to wait}
 */
const waitToDo = (time: number, cb: () => void): void => {
	setTimeout(() =>  {
		cb()
	}, time)
}

export {
	waitToDo,
}
